package NumberProgrammesJava;

public class ComputQuotientandremainder {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int dividend = 40;
		int divisor = 4;
		float quont = dividend / divisor;
		int rem = dividend % divisor;
		System.out.println(quont + " and " + rem);
	}

}
